func BenchmarkProtobuf(b *testing.B) {
	block := getBlock(b)
	pb := block.ToProtoMessage()
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		_, _ = proto.Marshal(pb)
	}
}

func BenchmarkRlp(b *testing.B) {
	block := getBlock(b)
	exB := exBlock{
		H: block.header,
		B: block.body,
	}
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		_, _ = rlp.EncodeToBytes(&exB)
	}
}

func BenchmarkJson(b *testing.B) {
	block := getBlock(b)
	exB := exBlock{
		H: block.header,
		B: block.body,
	}
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		_, _ = json.Marshal(exB)
	}
}

func BenchmarkProtobufUnmarshal(b *testing.B) {
	//sBlock := ""
	sBlock := ""
	bBlock, err := hex.DecodeString(sBlock)
	if err != nil {
		b.Fatal(err)
	}
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		var block types_pb.Block
		_ = proto.Unmarshal(bBlock, &block)
	}
}

func BenchmarkRlpDecode(b *testing.B) {
	block := getBlock(b)
	exB := exBlock{
		H: block.header,
		B: block.body,
	}

	buf, err := rlp.EncodeToBytes(&exB)
	if err != nil {
		b.Fatal(err)
	}

	b.ResetTimer()

	for i := 0; i < b.N; i++ {
		var exB exBlock
		_ = rlp.DecodeBytes(buf, &exB)
	}
}

func BenchmarkJsonDecode(b *testing.B) {
	block := getBlock(b)
	exB := exBlock{
		H: block.header,
		B: block.body,
	}
	bytes, err := json.Marshal(exB)
	if err != nil {
		b.Fatal(err)
	}
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		var exB exBlock
		_ = json.Unmarshal(bytes, &exB)
	}
}
